      <Box
        w={`${diameter}px`}
        // bg="red.100"
        overflow="hidden"
        h={`${diameter / 2}px`}
        position={"relative"}
      >
        <Box
          nativeID={showMenu ? "menu" : hideMenu ? "menu-close" : "none"}
          bottom={`-${diameter / 2}px`}
          right={0}
          left={0}
          mx="auto"
          position={"absolute"}
          h={`${diameter}px`}
          w={`${diameter}px`}
          style={{ transform: [{ rotate: hideMenu ? "0deg" : "180deg" }] }}
        >
          <Box
            bg="coolGray.100"
            w={`${diameter}px`}
            h={`${diameter / 2}px`}
            roundedTop="full"
            overflow={"hidden"}
            flexDir={"row"}
            position={"relative"}
          >
            {/* First Box */}
            <Box position={"absolute"}>
              <Box
                bg="red.300"
                size={`${diameter / 2}px`}
                roundedTopLeft={"full"}
                position={"relative"}
              >
                <Box bottom={0} position={"absolute"}>
                  Hello
                </Box>
              </Box>
            </Box>
            {/* Second Box */}
            <Box
              position={"absolute"}
              style={{
                transform: [
                  { translateY: `-${diameter / 4 + 40}px` },
                  { rotate: "45deg" },
                  { translateX: `${diameter / 2 - 60}px` },
                ],
              }}
            >
              <Box
                bg="blue.300"
                size={`${diameter / 2}px`}
                roundedTopLeft={"full"}
                position={"relative"}
              >
                <Box bottom={0} position={"absolute"}>
                  Hello
                </Box>
              </Box>
            </Box>
            {/* Third Box */}
